import CartContext from "./cartContext";
import { useReducer } from "react";

/*an item structure:
id: props.id,
name: props.name,
amount: amount,
price: props.price
*/


const defaultCart = {
  items: [],
  totalAmount: 0,
};


//reducer function needs a state snapshot (the last state), and an action
const cartReducer = (state, action) => {


 if (action.type === "ADD_ITEM") {

const existingItemInd = state.items.findIndex(item => item.id === action.item.id);

//if the item exists in the items array, it will return an index. Otherwise it is null.
const existingItem = state.items[existingItemInd]

let updatedItem;
let updatedItems;
const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;

//If the item does exist in the array, update the amount of the item, then update that item in the array
if (existingItem) {

  updatedItem = {...existingItem, 
  amount: existingItem.amount + action.item.amount}
  updatedItems = [...state.items];
  updatedItems[existingItemInd] = updatedItem
}

else {
  updatedItem = {...action.item};
  updatedItems = state.items.concat(action.item)
}

return {
    items: updatedItems,
    totalAmount: updatedTotalAmount
}
 }

 if (action.type === "REMOVE_ITEM") {

  const existingItemInd = state.items.findIndex(item => item.id === action.item.id);
  const existingItem = state.items[existingItemInd]

  if (existingItem){
  const updatedTotalAmount = state.totalAmount - existingItem.price}

  let updatedItems;

  if (existingItem.amount === 1) {
updatedItems = state.items.filter(item => item.id !== action.id);
  }
  else {
const updatedItem = {...existingItem, amount: existingItem.amount - 1};
updatedItems = [...state.items];
updatedItems[existingItemInd] = updatedItem;
  }

  return {
    items: updatedItems,
    totalAmount: updatedTotalAmount
  }

}

return defaultCart;
};

//Cart Provider component will allow things wrapped in CartProvider access to the Cart Context store.

const CartProvider = (props) => {


  const [cartState, dispatchAction] = useReducer(cartReducer, defaultCart);

  //reformat by taking the cartState to include the methods then send that value over to the cartContext store.
  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: (item) => {addItemHandler(item)},
    removeItem: (id) => {removeItemHandler(id)}
  };

  const addItemHandler = (item) => {
    dispatchAction({
      type: "ADD_ITEM",
      item: item,
    });
  };

  const removeItemHandler = (item) => {
    dispatchAction({
        type: "REMOVE_ITEM",
        item: item,
      });
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
